mapper.select:
select distinct rm.menu_id from sys_user_role ur
	LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id 
where ur.user_id = #{userId}

select t.user_id, t.sex, t.real_name, t.user_name, t.org_no, t.email, t.mobile, t.status, t.create_time,t.create_user_id,t.create_user_org_no from sys_user t where 1=1
and t.user_name like '%${userName}%'
and t.org_no = #{orgNo}
